package com.tns.day2;

public class LoopStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int i=0;
		  int r=0;
	      while (i<=10)
	      {
	        System.out.println(i);
	        i++;
	      }
	      
	      for (r=0;r<=10;r++)
	       {
	         System.out.println(r);
	       }
	      do
	      {
	        System.out.println(i);
	        i++;
	      }while(i<=10);

	}
 
}
